var koa = require('koa');
var app = koa();
var path = require('path');
var router = require('koa-router')();
var nunjucks = require('nunjucks');
var env = nunjucks.configure(__dirname, {
    autoescape: false,
    noCache: true
});
router.get('/', function *(next) {
	this.body = env.render('./a.html', {
		title: 'test'
	});
});

app
  .use(router.routes())
  .use(router.allowedMethods());
app.listen(3000);
