var fs = require('fs-extra');
module.exports = {
    /**
     *
     * @param filePath json文件路径
     * @param defaultData 如果文件不存在时,指定默认值; 若不指定且json文件不存在时,返回{}
     * @returns {*} 返回读取到的json或指定的默认值
     */
    getJson: function (filePath, defaultData) {
        defaultData = (typeof defaultData === 'undefined') ? {} : defaultData;
        if (fs.existsSync(filePath)) {
            try {
                return fs.readJsonSync(filePath);
            } catch (err) {
                return defaultData;
            }
        } else {
            return defaultData;
        }
    },

    /**
     * 判断文件是否存在
     * @param filePath
     * @returns {*}
     */
    exists: function (filePath) {
        return fs.existsSync(filePath);
    },

    /**
     * 替换字符串里面的占位符
     * @param str
     * @param obj
     * @returns {*}
     */
    replacePlaceholder: function (str, obj) {
        obj = obj || {};
        if (str.homedir) {
            str = str.replace(/\{\{homedir\}\}/img, obj.homedir);
        }
        if (obj.site) {
            str = str.replace(/\{\{site\}\}/img, obj.site);
        }
        if (obj.project) {
            str = str.replace(/\{\{project\}\}/img, obj.project);
        }
        if (obj.module) {
            str = str.replace(/\{\{module\}\}/img, obj.module);
        }
        if (obj.version) {
            str = str.replace(/\{\{version\}\}/img, obj.version);
        }
        if (obj.target) {
            str = str.replace(/\{\{target\}\}/img, obj.target);
        }
        if (obj.router) {
            str = str.replace(/\{\{router\}\}/img, obj.router);
        }
        if (obj.domain) {
            str = str.replace(/\{\{domain\}\}/img, obj.domain);
        }
        return str;
    },

    /**
     * require最新的js模块
     * @param filePath
     * @returns {*|Object}
     */
    requireLatest: function (filePath) {
        delete require.cache[require.resolve(filePath)];
        return require(filePath);
    }

}
